nta.declaration:
// Global time. One time unit in Uppaal represents 1 minute
const int m = 1; // 1 minute = 1 time unit
const int h = 60*m; // 1 hour = 60 minutes
clock t = 0.0; // global time [min]. Starts at 5 October 2021 17:00.

// Urban catchment
const double UC_k = 0.25; // Urban surface resevoir reaction factor [cm/min * 1 / cm]
const double A_UC = 37.0 * 10000.0 * 10000.0; // urban area catchment [cm2], 1 ha = 10000 m2, 1 m2 = 10000 cm2 = [cm/(min*cm)]
double rain = 0.0; // Rainfall [cm/min]
clock S_UC = 0.0; // Water surface height urban catchment [cm]

// Rain detention pond
const int W = 300; // max water height [cm]
clock wp = 100.0; // water level in pond [cm]
double Qout = 0.0; // pond outflow [cm3/min], will be properly initialized in Controller
clock o = 0.0; // accumelated overflow time [min]

// Stream
clock ws = 0.0; // water level in stream [cm]
double test = 0.0;

// Cost function
const double alpha = 10000.0; // Overflow penalty factor
clock cost_sed = 0.0; // Particle sedimentation cost function [-]
clock cost_stream = 0.0; // Stream water level cost function [-]

// Import data reading functions
import "/home/martijn/Documents/CLAIRE/water_level/libtable.so" {
    /** read the table from the csv file and return its id: */
    int table_read_csv(const string& filename, int skip_lines);
    /** return the number of rows in the table: */
    int table_rows(int id);
    /** return the number of columns in the table: */
    int table_cols(int id);
    /** read an integer value at row:col, counted from 0: */
    int read_int(int id, int row, int col);
    /** read a double value at row:col, counted from 0: */
    double read_double(int id, int row, int col);
};

template.declaration:
// As we only know the pond's surface area, we assume that this is constant.
const double A_p = 5572 * 10000.0; // pond area [cm2], 1 m2 -> 10000 cm2

// Flow from urban catchment to pond. Eq. 2 in paper.
double Qin()
{
    return S_UC * UC_k * A_UC;
}

// Dynamics of the water level. Eqs. 4 and 5 in the paper combined.
double pond_dynamics()
{
    if (wp <= 0 && Qin() <= Qout) return 0;

    else if (wp >= W && Qin() >= Qout) return 0; 

    else return (Qin() - Qout) / A_p;
}

// Cummulative overflow duration. Eq. 6 in the paper.
double overflow()
{
    const double epsilon = 0.1; // for numerical precision 
    if (wp >= W - epsilon) return 1.0;

    else return 0.0;
}

const int I = 2819; // number of intervals. Simulation should finish before the last period is over.
const int i_init = 0; // Constant introduced, otherwise the initial boundaries cannot be determined.
int i = i_init; // current time interval index
clock d = 0.0; // duration of dry or raining period [min]
int time_step = 5;

// Load water level predictions data from external data file.
const int file_id = table_read_csv("/home/martijn/Documents/CLAIRE/water_level/water_data_test_pred.csv", 1);
const int rows = table_rows(file_id);
const int cols = table_cols(file_id);

// TODO add out-of-range checks below for safety.
double get_level(int time_interval)
{
    // Predicted water level should be in the 8th column.
    return read_double(file_id, time_interval, 8);
}

double get_std(int time_interval)
{
    // Predicted standard deviation should be in the 9t column.
    return read_double(file_id, time_interval, 9);
}

int get_time_interval()
{
    return fint(floor(t / time_step));
}

// Returns a sample from a uniform distribution between lower and upper.
double uniform(double lower, double upper)
{
    double distance = upper - lower;
    double sample = random(distance);
    return lower + sample;
}

// TODO In Stratego 10 use new built-in distributions.
double sample_level(int time_interval)
{
    double mean = get_level(time_interval);
    double std = get_std(time_interval);

    return uniform(mean - std, mean + std);
}

// Initialize the variables.
double level = sample_level(i_init);

// Stream cost function constants.
const double w1 = 5.0; // Below this level we will penelize drying of stream.
const double w2 = 30.0; // Above this level we will penelize overflow of stream.
const double c1 = 2.0; // Cost contribution when stream is flully dry.
const double c2 = 1.0; // Cost contribution at w2 level.

clock x = 0.0;

// Cost contribution when we only consider stream overflow.
double only_overflow()
{
    if (ws <= w2) return c2 / w2 * ws;

    else return c2 / (w2 * w2) * pow(ws - w2, 2) + c2 / w2 * ws;
}

// Cost contribution when we consider stream overflow and stream drying.
double overflow_and_dry()
{
    if (w1 <= ws && ws <= w2) return c2 / w2 * ws;

    else if (ws > w2) return c2 / (w2 * w2) * pow(ws - w2, 2) + c2 / w2 * ws;

    else return c1 / (w1 * w1) * (w1 - ws) * (w1 - ws) - c2 / w2 * (w1 - ws) + c2 / w2 * w1;
}

const int I = 6; // number of intervals. Simulation should finish before the last period is over. Therefore, last period is a large dummy period.
const int i_init = 0; // Constant introduced, otherwise the initial boundaries cannot be determined.
int i = i_init; // current interval index
clock d = 0.0; // duration of dry or raining period [min]

